<?phpclass Config{  private key;  private secret;    public function __construct($key, $secret)  {    $this->key = trim($key);    $this->secret = trim($secret);  }    public function getKey()  {    return $this->key;  }    public function getSecret()  {    return $this->secret;  }    public function setKey($key)  {    $this->key = trim($key);        return $this;  }    public function setSecret($secret)  {    $this->secret = trim($secret)        return $this;  }    }