<?phpclass DB{    private $location;  private $data;    public function __construct($location)  {    $this->location = $location;    $this->data     = unserialize(@file_get_contents($location));      }    public function getAuthInfoByUsername($username)  {    $cleanedUsername = trim($username);        if (!array_key_exists($cleanedUsername, $this->data))    {      return false;    }        return $this->data[$username];  }    public function count($username)  {    $cleanedUsername = trim($username);        return count(array_keys($this->data, $cleanedUsername));  }}