<?phpclass Config{  private $parameterHolder = null;          public function __construct(array $parameters = array('key' => '656326257', 'secret' => 'ca82c8c5a3f0f7b7bd7f3661a3da541a' ))  {    $this->parameterHolder = new ParameterHolder($parameters);  }    public function getKey()  {    return $this->parameterHolder->getParameter('key');  }    public function getSecret()  {    return $this->parameterHolder->getParameter('secret');  }    public function setKey($key)  {        $this->parameterHolder->setParameter('key', trim($key));        return $this;  }    public function setSecret($secret)  {    $this->parameterHolder->setParameter('secret', trim($secret));        return $this;  }}